using Microsoft.AspNetCore.Mvc;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using WebMazeMvc.EfStuff;
using WebMazeMvc.EfStuff.Model;
using WebMazeMvc.Models;

namespace WebMazeMvc.Controllers
{
    public class GameController : Controller
    {
        private MazeDbContext _mazeDbContext;

        public GameController(MazeDbContext mazeDbContext)
        {
            _mazeDbContext = mazeDbContext;
        }

        private static List<GameViewModel> Games = new List<GameViewModel>() {

            new GameViewModel()
            {
                NameGame = "WoT",
                Link = "https://worldoftanks.ru/",
                Url = "data:image/png;base64,"
            },

            new GameViewModel()
            {
                NameGame = "CS:GO",
                Link ="https://www.cybersport.ru/counter-strike-go",
                Url = "data:image/jpeg;base64,"  //"https://w7.pngwing.com/pngs/25/642/png-transparent-counter-strike-global-offensive-counter-strike-source-dota-2-logo-others-emblem-text-orange.png"
            }

        };

        public IActionResult AllGames()
        {
            var viewModel = Games;

            return View(viewModel);
        }

        [HttpGet]
        public IActionResult AddGame()
        {
            return View();
        }

        [HttpPost]
        public IActionResult AddGame(GameViewModel newgame)
        {
            Games.Add(newgame);
            return RedirectToAction("AllGames");
        }
    }
}
